import React, { useState, useEffect } from "react";
import axios from "axios";
import InputGroup from "react-bootstrap/InputGroup";
import FormControl from "react-bootstrap/FormControl";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

class Registration extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      username: "",
      email: "",
      password: "",
      profile_photo: ""
    };
  }

  // handleUsernameChange = (event) => {
  //   this.setState({
  //     username: event.target.value
  //   })
  // }

  // handleEmailChange = (event) => {
  //   this.setState({
  //     email: event.target.value
  //   })
  // }

  // handlePasswordChange = (event) => {
  //   this.setState({
  //     password: event.target.value
  //   })
  // }

  // handleProfilePhotoChange = (event) => {
  //   this.setState({
  //     profile_photo: event.target.value
  //   })
  // }

  handleSubmit = (event) => {
    alert(`${this.state.username} ${this.state.email} `)
    event.preventDefault()
  }

  render() {
    // const {username, email, password, profile_photo} = this.state

    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          {/* <input
            type="text"
            name="username"
            placeholder="Username"
            value={username}
            onChange={this.handleUsernameChange}
            required
          /> */}
<h1>Join InstaMiligram Today</h1>

          <Form.Group controlId="formBasicUsername" className="w-25 p-3">
            <Form.Label>Username</Form.Label>
            <Form.Control type="text" placeholder="Enter username" required value={username} />
          </Form.Group>

          <Form.Group controlId="formBasicEmail" className="w-25 p-3">
            <Form.Label>Email address</Form.Label>
            <Form.Control type="email" placeholder="Enter email" required value={email} />
            <Form.Text className="text-muted">
              We'll never share your email with anyone else.
            </Form.Text>
          </Form.Group>

          <Form.Group controlId="formBasicPassword" className="w-25 p-3">
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" placeholder="Password" required value={password} />
            <Form.Text id="passwordHelpBlock" muted>
      Must be 8-20 characters long.
    </Form.Text>
          </Form.Group>
          {/* <Form.Group controlId="formBasicCheckbox">
    <Form.Check type="checkbox" label="Check me out" />
  </Form.Group> */}
          <Form.Group controlId="formBasicProfilePhoto" className="w-25 p-3">
            <Form.Label>Profile Photo (URL)</Form.Label>
            <Form.Control type="text" placeholder="Profile Photo" required value={profile_photo} />
          </Form.Group>
          <Button variant="primary" type="submit">
            Submit
          </Button>
          {/* <input
            type="text"
            name="profile-photo"
            placeholder="Profile Photo (URL)"
            value={profile_photo}
            onChange={this.handleProfilePhotoChange}
            required
          />

          <button type="submit">Register</button> */}
        </form>
      </div>
    );
  }
}

export default Registration;
